mapper.insert:

	select mileage_seq.nextval from dual


insert into mileage(mileageNo, userid, savingPoint
	, payNo, usePoint, endDate)
values(#{mileageNo}, #{userid}, #{savingPoint}
	, #{payNo}, #{usePoint}
	
		, sysdate+(interval '2' year)
	
	)

mapper.select:
select * from ( select rownum as RNUM, A.* from ( select mileageno,userid,savingdate,savingpoint,payno,userpoint,enddate from mileage where ( ADD_MONTHS(SYSDATE, -12)<enddate )
order by mileageno desc )A ) where RNUM > #{firstRecordIndex}
and RNUM<= #{firstRecordIndex} + #{recordCountPerPage}
select * from mileage where ( ADD_MONTHS(SYSDATE, -12)<enddate ) order by mileageno
